import java.util.ArrayList;
import java.util.List;

public class Solution {
    public static List<Integer> predecessorSuccessor(TreeNode root, int key) {
        int predecessor = -1;
        int successor = -1;

        // Initializing temporary node with head.
        TreeNode temp = root;

        // Traversing in tree.
        while (temp != null)
        {
            if (key > temp.data)
            {
                // Updating predecessor.
                predecessor = temp.data;
                temp = temp.right;
            }
            else
            {
                temp = temp.left;
            }
        }

        temp = root;

        // Traversing in tree.
        while (temp != null)
        {
            if (key >= temp.data)
            {
                temp = temp.right;
            }
            else
            {
                // Updating successor.
                successor = temp.data;
                temp = temp.left;
            }
        }

        List< Integer > sol = new ArrayList <> ();
        sol.add(predecessor);
        sol.add(successor);
        return sol;
    }
}

