/*

    Time Complexity : O(1)
    Space Complexity : O(1)


*/


#include <utility>

pair<char, int> averageMarks(char firstLetterOfName, int m1, int m2, int m3) {
    // Find the total marks.
    int total = m1 + m2 + m3;
    // Find the average.
    int average = total / 3;

    pair<char, int> ans;
    ans.first = firstLetterOfName;
    ans.second = average;
    return ans;

}

/*

    Time Complexity : O(1)
    Space Complexity : O(1)


*/


public class Solution {

    public static Pair averageMarks(char firstLetterOfName, int m1, int m2, int m3) {

        // Find the total marks.
        int total = m1 + m2 + m3;
        // Find the average.
        int average = total / 3;

        return new Pair(firstLetterOfName, average);

    }
}
