/*
    Time Complexity : O(N*log^2(M))
    Space Complexity : O(1)

    Where 'N' is the number of cooks, 
    and 'M' is the number of dishes.
*/

#include <cmath>

int minCookTime(vector<int> &rank, int m)
{
    int low = 1, high = 5 * m * (m + 1);

    // Binary Search
    while (high > low)
    {
        int mid = (high + low) / 2;
        int dishCooked = 0;

        // Calculate total number of dishes prepared in time = 'mid' by 'N' cooks.
        for (int i = 0; i < rank.size(); i++)
        {
            dishCooked += (-1 + sqrt(1 + (8 * mid) / rank[i])) / 2;
        }

        if (dishCooked < m)
        {
            low = mid + 1;
        }
        else
        {
            high = mid;
        }
    }

    return high;
}

/*
    Time Complexity : O(N*log^2(M))
    Space Complexity : O(1)

    Where 'N' is the number of cooks, 
    and 'M' is the number of dishes.
*/

import java.util.ArrayList;

public class Solution
{
    public static int minCookTime(ArrayList<Integer> rank, int m)
    {
        int low = 1, high = 5 * m * (m + 1);

        // Binary Search
        while (high > low)
        {
            int mid = (high + low) / 2;
            int dishCooked = 0;

            // Calculate total number of dishes prepared in time = 'mid' by 'N' cooks.
            for (int i = 0; i < rank.size(); i++)
            {
                dishCooked += (-1 + Math.sqrt(1 + (8 * mid) / rank.get(i))) / 2;
            }

            if (dishCooked < m)
            {
                low = mid + 1;
            }
            else
            {
                high = mid;
            }
        }

        return high;
    }
}
