#include <iostream>

using namespace std;

int main() {
    int n, fact;
    cin >> n;
    if (n < 0)
        cout << "Error";
    else {
        fact = 1;
        for (int i = 2; i <= n; i++)
            fact *= i;
        cout << fact;
    }

    return 0;
}

