import java.util.Scanner;

class Solution {
	
	public static void main(String args[]) {
		
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		
		if(n < 0) {
			System.out.println("Error");
		}
		
		else {
			int fact = 1;
			for(int i = 2; i <= n; i++) {
				fact *= i;
			}
			
			System.out.println(fact);	
		}
		
	}
}
