/*
    Time Complexity: O(N)
    Space Complexity: O(1)

    Where N is the size of array.
*/

int minSubarraySum(int arr[], int n, int k)
{
    int minimumSum = 0;

    // Computing sum of first window/subarray of size k
    for (int i = 0; i < k; i++)
    {
        minimumSum += arr[i];
    }

    int currWindowSum = minimumSum;

    /*
     Computing the sum of rest subarrays of size 'k' 
     by removing the first element of previous window/subarray 
     and adding the current element to the window.
    */
    for (int i = k; i < n; i++)
    {
        currWindowSum += arr[i] - arr[i - k];

        minimumSum = min(minimumSum, currWindowSum);
    }

    return minimumSum;
}

/*
    Time Complexity: O(N)
    Space Complexity: O(1)

    Where N is the size of array.
*/

import java.util.*;

public class Solution
{
public static int minSubarraySum(ArrayList<Integer> arr, int n, int k)
    {
        int minimumSum = 0;

        // Computing sum of first window/subarray of size k
        for (int i = 0; i < k; i++)
        {
            minimumSum += arr.get(i);
        }

        int currWindowSum = minimumSum;

        /*
        Computing the sum of rest subarrays of size 'k' 
        by removing the first element of previous window/subarray 
        and adding the current element to the window.
        */
        
        for (int i = k; i < n; i++)
        {
            currWindowSum += arr.get(i) - arr.get(i - k);

            minimumSum = Math.min(minimumSum, currWindowSum);
        }

        return minimumSum;
    }
}
