#include<iostream>

using namespace std;

int main() {
    int x, n;
    cin >> x >> n;
    int ans = 1;
    while (n > 0) {
        ans *= x;
        n--;
    }
    cout << ans;
}

java.full_func:
import java.util.Scanner;

class Solution {
	
	public static void main(String args[]) {
		
		Scanner sc = new Scanner(System.in);
		int x = sc.nextInt();
		int n = sc.nextInt();
		
		int ans = 1;
		
		while(n > 0) {
			ans *= x;
			n--;
		}
		
		System.out.println(ans);
		
	}
}
