#include<iostream>

using namespace std;

int main() {

    int x, y;
    cin >> x >> y;
    if (x > 0 && y > 0)
        cout << "1st Quadrant";
    else if (x < 0 && y > 0)
        cout << "2nd Quadrant";
    else if (x < 0 && y < 0)
        cout << "3rd Quadrant";
    else if (x > 0 && y < 0)
        cout << "4th Quadrant";
    else if (x == 0 && y == 0)
        cout << "Origin";
    else if (x == 0 && y != 0)
        cout << "y axis";
    else
        cout << "x axis";
    return 0;
}

