import java.util.*;

public class Main {


	public static void main(String[] args) {
		Scanner scn = new Scanner(System.in);
        int N = scn.nextInt();
        int w[] = new int[N];
        for (int i = 0; i < N; i++) {
            w[i] = scn.nextInt();
        }
        Arrays.sort(w);
        int cost = 1;
        int last = w[0];
        for (int i = 1; i < N; i++) {
            if (w[i] > last + 4) {
                cost++;
                last = w[i];
            }
        }
        System.out.println(cost);

	}

}
