/*
    Time complexity : O(log(N))
    Space complexity : O(1)

    Where 'N' is the number of elements in the array.
*/

int missingK(vector<int> vec, int n, int k)
{
    int low = 0, high = n - 1; 

    // Perform binary search.
    while(low <= high) 
    {
        int mid = (low + high) / 2; 
        int missing = vec[mid] - (mid + 1); 
        
        // If missing element exists between vec[low] and vec[high].
        if(missing < k) 
        {
            low = mid + 1; 
        }

        // If missing element exists after the sequence.
        else 
        {
            high = mid - 1; 
        }
    }
    return low + k;
}

