/*

	Time complexity: O(N)
	Space complexity: O(N)

	where 'N' is the size of the array.

*/

int maxDiff(vector<int> a, int n)
{
    // Create auxiliary vector.
    vector <int> RMax (n, 0);

    RMax[n - 1] = a[n -1] ;

    // Store greatest element from right.
    for(int j = n - 2; j >= 0; j--)
    {
        RMax[j] = max(a[j], RMax[j+1]);
    }

    int i = 0, j = 0;
    int diff = -1;

    // Start comparing both vectors.
    while (j < n && i < n)
    {
        // If greater element is found on right side as per the given condition.
        if (a[i] <= RMax[j])
        {
            // Update diff.
            diff = max(diff, j - i);
            j = j + 1;
        }
        else
        {
            // Move a step ahead in given array.
            i = i + 1;
        }
    }

    return diff;
}

/*

	Time complexity: O(N)
	Space complexity: O(N)

	where 'N' is the size of the array.

*/

import java.util.ArrayList;

public class Solution {
	public static int maxDiff(ArrayList<Integer> a, int n) {

		// Create an auxiliary ArrayList.
		ArrayList<Integer> RMax = new ArrayList<Integer>(n);

		for (int i = 0; i < n; i++) {
			RMax.add(0);
		}

		RMax.set(n - 1, a.get(n - 1));

		// Store greatest element from right.
		for (int j = n - 2; j >= 0; j--) {
			RMax.set(j, Math.max(a.get(j), RMax.get(j + 1)));
		}

		int i = 0, j = 0;
		int diff = -1;

		// Start comparing both ArrayLists.
		while (j < n && i < n) {

			if (a.get(i).intValue() <= RMax.get(j).intValue()) {
				diff = Math.max(diff, j - i);
				j = j + 1;
			} else {
				// Move a step ahead in given array.
				i = i + 1;
			}

		}

		return diff;
	}

}
