/*
    Time Complexity : O(N)
    Space Complexity : O(1)

    Where ‘N’ is the number of elements in the array/list.
*/

int maximumNonAdjacentSum(vector<int> &nums){
    int n = nums.size();

    // Two variables to store the previous results.
    int prevFirst = 0;
    int prevSecond = 0;

    for (int i = 0; i < n; i++){
        int temp = prevFirst;
        prevFirst = max(prevSecond + nums[i], prevFirst);
        prevSecond = temp;
    }
    return prevFirst;
}

/*
    Time Complexity : O(N)
    Space Complexity : O(1)

    Where 'N' is the number of elements in the array/list.
*/

import java.util.ArrayList;

public class Solution {
	public static int maximumNonAdjacentSum(ArrayList<Integer> nums) {
		int n = nums.size();

		// Two variables to store the previous results.
		int prevFirst = 0;
		int prevSecond = 0;

		for (int i = 0; i < n; i++) {
			int temp = prevFirst;

			prevFirst = Math.max(prevSecond + nums.get(i), prevFirst);
			prevSecond = temp;
		}
		return prevFirst;
	}
}
