/*
   Time Complexity: O(N * logM)

   Space Complexity: O(1)

    where N is the number of piles,
    and M is the integer(1e9).
*/
public class Solution {
    public static int minimumRateToEatBananas(int []v, int h) {
        // Number of piles of bananas
        int n = v.length;

        // Lower and Upper bound for the rate to
        // eat bananas
        long s = 1, e = (long)1000000000;
        // long long is used to avoid overflow in some 
        // scenario

        // "ans" will store the minimum rate to
        // eat bananas
        int ans = -1;

        // Applying Binary Search
        while(s <= e) {
            long mid = (s + e) >> 1;

            // We assume that the rate to eat bananas
            // is "mid"
            // Now, we calculate the time taken to eat all 
            // bananas
            // It will be stored in "cnt"
            long cnt = 0;
            for(int i = 0; i < n; ++i) {
                int p = v[i];

                // Adding ceil value
                cnt += (p + mid - 1) / mid;
            }

            if(cnt <= h) {
                // With this rate, we can eat all bananas
                // within the time
                ans = (int)mid;
                // Searching for smaller rate
                e = mid - 1;
            }
            else {
                // Searching for higher rate
                s = mid + 1;
            }
        }
        return ans;
    }
}
