/*
    Time Complexity: O( log(maximum of A and B) ) 
    Space Complexity: O(1)

    where ‘A’ and ‘B’ are the given numbers.
*/

int numberOfFlips(int a, int b)
{
    // ans will store the number of bits to be flipped.
    int ans = 0;

    // c is equal to a XOR b
    int c = (a ^ b);

    while (c > 0){

        if (c % 2 == 1){
            // Set bit found.
            ans = ans + 1;
        }

        c = c/2;
    }

    return ans;

}

/*
	Time Complexity: O( log(maximum of A and B) ) 
	Space Complexity: O(1)

	where A and B are the given numbers.
*/

public class Solution {

	public static int numberOfFlips(int a, int b) {

		// ans will store the number of bits to be flipped.
		int ans = 0;

		// c is equal to a XOR b
		int c = (a ^ b);

		while (c > 0) {

			if (c % 2 == 1) {

				// Set bit found.
				ans = ans + 1;
			}

			c = c / 2;
		}

		return ans;
	}

}
