#include<iostream>

using namespace std;

class Person {

    private:
        string name;
    int age;

    public:

        void setValue(string a, int b) {
            name = a;
            age = b;
        }

    void getValue() {
        cout << "The name of the person is " << name << " and the age is " << age << ".";
    }
};

int main() {

    Person obj;
    string name;
    int age;
    cin >> name >> age;
    obj.setValue(name, age);
    obj.getValue();

    return 0;
}

java.full_func:
import java.util.Scanner;
class Person {
	
	private String name;
	private int age;
	
	public void setValue(String name, int age) {
		this.name = name;
		this.age = age;
	}
	
	public void getValue() {
		System.out.println("The name of the person is " + name + " and the age is " + age + ".");
	}
}

class Solution {
	
	public static void main(String args[]) {
		
		Person obj = new Person();
		Scanner sc = new Scanner(System.in);
		String name = sc.nextLine();
		int age = sc.nextInt();
		obj.setValue(name, age);
		obj.getValue();
	}
}
