/*

    Time complexity: O(log(N)) 
    Space complexity: O(1)

    where 'N' is the given integer.

*/

#include <iostream>

using namespace std;

int countBits(int N) {

    int count = 0;
    int r;

    while (N > 0) {

        r = N % 2;

        if (r == 1)
        {
            count++;
        }

        N = N / 2;
    }

    return count;
}

int main() {
    int n;
    cin >> n;
    cout << countBits(n);
    return 0;
}

def countBits(n):

    count = 0

    while (n > 0):

        r = n % 2
        if r == 1:
            count += 1

        n = n // 2

    return count




