/*
    Time Complexity: O(N)
    Space Complexity: O(1)

    Where N is the length of the string.
*/

#include <unordered_set>

bool splitString(string &str){

    // Set of vowels.
    unordered_set<char> vowels = {'a', 'e', 'i', 'o', 'u', 'A', 'E', 'I', 'O', 'U'};

    // Second half of the string.
    int secondHalf = str.size() / 2;

    // Difference between vowels in both half.
    int diff = 0;

    for(int i = 0; i < secondHalf; i++){

        // If vowels is found in first half.
        if(vowels.count(str[i]) != 0){
            diff += 1;
        }

        // If vowels is found in second half.
        if(vowels.count(str[i + secondHalf]) != 0){
            diff -= 1;
        }
    }

    return diff == 0;
}

