/*
    Time Complexity: O(N)
    Space Complexity: O(1)

    Where N is the length of the string.
*/

import java.util.HashSet;

public class Solution {

    public static Boolean splitString(String str) {

        HashSet<Character> vowels = new HashSet<Character>();
        vowels.add('a');
        vowels.add('e');
        vowels.add('i');
        vowels.add('o');
        vowels.add('u');
        vowels.add('A');
        vowels.add('E');
        vowels.add('I');
        vowels.add('O');
        vowels.add('U');

        // Second half of the string.
        int secondHalf = str.length() / 2;

        // Difference between vowels in both half.
        int diff = 0;

        for (int i = 0; i < secondHalf; i++) {

            // If vowels is found in first half.
            if (vowels.contains(str.charAt(i)) == true) {
                diff += 1;
            }

            // If vowels is found in second half.
            if (vowels.contains(str.charAt(i + secondHalf)) == true) {
                diff -= 1;
            }
        }

        return diff == 0;
    }
}
