public class Solution{
    public static void swapNumber(int []a, int []b) {
        /*
            Store the sum of the 2 variables in 'a'.
            Assign difference of 'a' and 'b' to 'b' and difference of 'a' and 'b' to 'a'.
        */
        a[0] = a[0] + b[0];
        b[0] = a[0] - b[0];
        a[0] = a[0] - b[0];
    }
}
