class Solution {
  public:
    int subClock(int num1, int num2) {
        
        int ans = num1-num2;    // Subtracting num2 from num1
        ans %= 12;    // Taking modulo of ans with 12
        if(ans<0)
            ans+=12;    // If ans is negative, add 12 to make it positive
        
        return ans;    // Return the answer
    }
};

