class Solution {
    static int subClock(int num1, int num2) {

        int ans = num1-num2;    // Subtracting num2 from num1
        ans %= 12;              // Taking modulus of ans with 12
        if(ans<0)               // Checking if ans is negative
            ans+=12;            // Adding 12 to ans if it is negative

        return ans;             // Returning the answer
    }
};
