class Solution{
public:
    string onesComplement(string S,int N){
        string ans = "";
        // for every character in S
        for(int i = 0;i<N;i++)
        {
            // if character at i is 0 then add character 1 to ans
            if(S[i] == '0')
                ans = ans + '1';
            // if character at i is 1 then add character 0 to ans
            else
                ans = ans + '0';
        }
        return ans;
    }
};

