class Solution {
  public:
    // Helper method to reverse a Number
    int reversedNumber(int num) {
        int rev = 0;
        while (num > 0)
        {
            rev = rev * 10 + num % 10;
            num /= 10;
        }
        return rev;
    }
    
    string checkAdamOrNot(int N) {
        
        int r = reversedNumber(N);   // Reverse of N
        
        int a = N*N;    // Storing Sqauare of N in a
        
        int b = reversedNumber(r*r);    // Storing reverse of the square of the reversed Number 
        
        // If a and b are equal, then Print YES, else print NO
        if(a==b)
            return "YES";
        else
            return "NO";
    }
};

