//Back-end complete function template for C++

class Solution{
public:

    //Function to calculate the sum of the submatrix.
    long long subMatrixSum(vector<int> arr[], int n, int m, int x1, int y1, int x2,
                       int y2) {
	    long long sum = 0;

        //iterating over the submatrix using the given coordinates.
	    for (int i = x1 - 1; i < x2; i++) {
	        for (int j = y1 - 1; j < y2; j++) {
	            sum += arr[i][j]; //adding the current element to the sum.
	        }
	    }
	    return sum; //returning the sum of the submatrix
	}

};
