// Back-end complete function Template for C++

class Solution{
public:
    // Function to calculate the integral root of N to the power K.
    long long integralRoot(int N, int K){
        // Calculating x and y using the pow function.
        long long x = pow(N, K);
        long long y = pow(N + 1, K);

        // Returning the difference between y and x.
        return (y - x);
    }
};
