//Back-end complete function template in C++

class Solution{
	public:
	//Function to determine the winner of the game.
	string Play(vector<int>nums, int k)
	{
		int cnt = 0;

		//iterating over each number in the given vector.
		for(auto i: nums){
			int x = i;

			//counting the number of times k can be divided into x.
			while(x){
				cnt++;
				x /= k;
			}
		}

		//checking if the count is odd or even and returning the winner.
		return (cnt&1) ? "Akash" : "Friend";
	}
};
