class Solution{
    public:
    string amendSentence (string s)
    {
    	int n = s.length ();
    	string res = "";

    	// making the first character uppercase
    	if (s[0] >= 'A' and s[0] <= 'Z')
    		res += (s[0] - 'A' + 'a');
    	else
    		res += s[0];

    	for (int i = 1; i < n; ++i)
    	{
    	    // if the character is lowercase, we append it as it is.
    		if (s[i] >= 'a' and s[i] <= 'z')
    			res += s[i];

    		// if it's uppercase, we append a space and the lowercase version of that character.
    		else
    		{
    			res += " ";
    			res += (s[i] - 'A' + 'a');
    		}
    	}
    	return res;
    }
};

class Solution
{
    public string amendSentence(string s)
    {
        int n = s.Length;
        StringBuilder res = new StringBuilder();

        if (s[0] >= 'A' && s[0] <= 'Z')
            res.Append((char)(s[0] - 'A' + 'a'));
        else
            res.Append(s[0]);

        for (int i = 1; i < n; ++i)
        {
            if (s[i] >= 'a' && s[i] <= 'z')
                res.Append(s[i]);
            else
            {
                res.Append(' ');
                res.Append((char)(s[i] - 'A' + 'a'));
            }
        }
        return res.ToString();
    }
}
