class Solution {
  public:
    // Helper method to get the sum of all proper divisors of a number.
    long long divsum(int n)
    {
        long long result = 0;
        for (int i = 2; i <= sqrt(n); i++)
        {
            if (n % i == 0)
            {
                if (i == (n / i))
                    result += i;
                else
                    result += (i + n/i);
            }
        }
        return (result + 1);
    }
    
    int isAmicable(int A , int B) {
        
        // If and only if sum of proper divisors of A is equal to B 
        // and sum of Proper divisors of B is equal to A, we return 1.
        if(A!=B && divsum(A)==B && divsum(B)==A)    
            return 1;
        else
            return 0;
    }
};

