// Backend complete function template for C++

class Solution {
  public:
    // Function to solve the problem
    int easyProblem(int K, int L, int R, int X, int Y) {
        int flag = 0;

        // Iterating through all possible values of B
        for (int B = X; B <= Y; B++) {
            int A = K * B;

            // Checking if A is greater than R, no need to continue iterating
            if (A > R) break;

            // Checking if A is within the range [L, R]
            if (A >= L && A <= R) {
                flag = 1;
                break;
            }
        }
        return flag;
    }
};

