// Back-end complete function Template for Java

class Solution {
    // function to check if there exists a number between X and Y
    // such that it satisfies A = B * K and A is between L and R
    static int easyProblem(int K, int L, int R, int X, int Y) {
        int flag = 0; // flag variable to indicate if condition is satisfied
        for (int B = X; B <= Y; B++) { // loop through all numbers between X and Y
            int A = B * K; // calculate A using the formula A = B * K
            if (A > R) break; // if A is greater than R, exit the loop
            if (A >= L && A <= R) { // check if A is between L and R
                flag = 1; // set flag to 1 indicating the condition is satisfied
                break; // exit the loop
            }
        }
        return flag; // return the value of flag
    }
}
