class Solution {
  public:
    int getAngle(int h, int m) {

        if (h == 12)
            h = 0;
        if (m == 60)
            m = 0;

        // Calculate the angles moved
        // by hour and minute hands
        // with reference to 12:00
        double hour_ang = 0.5 * (h * 60 + m);
        double min_ang = 6 * m;

        double angle = (abs(hour_ang - min_ang));

        // Getting the minimum of 2 angles
        if (angle > 180)
            angle = (int)(360 - angle);
        else
            angle = (int)angle;

        return angle;
    }
};

