javascript.full_func:
// User function Template for javascript

/**
 * @param {number} h
 * @param {number} m
 * @return {number}
 */

class Solution {
    getAngle(h, m) {
        if (h == 12) h = 0;
        if (m == 60) m = 0;

        // Calculate the angles moved
        // by hour and minute hands
        // with reference to 12:00
        let hour_ang = 0.5 * (h * 60 + m);
        let min_ang = 6 * m;

        let angle = Math.abs(hour_ang - min_ang);

        // Getting the minimum of 2 angles
        if (angle > 180) angle = 360 - angle;

        return Math.floor(angle);
    }
}
