class Solution{
    // Method to check if a given number is divisible by 5
    static String isValid(Long N){
        // Checking if the number is divisible by 5 using modulo operator
        if(N%5==0)
            // If the number is divisible by 5, return "YES"
            return "YES";
        else
            // If the number is not divisible by 5, return "NO"
            return "NO";
    }
}

