#include <iostream>
using namespace std;

int main() {
    int a, d, n;
    cin >> a >> d >> n;
    int ans = a + (n - 1) * d;
    cout << ans << endl;
    return 0;
}

import java.util.Scanner;

class Main {
    public static void main(String[] args) {
        Scanner scn = new Scanner(System.in);
        int a = scn.nextInt();
        int d = scn.nextInt();
        int n = scn.nextInt();
        int ans = a + (n - 1) * d;
        System.out.println(ans);
    }
}
