// Back-end complete function Template for C++

class Solution {
  public:
    // Function to calculate the intersection area of two circles.
    long long int intersectionArea(long double X1, long double Y1,
                                   long double R1, long double X2,
                                   long double Y2, long double R2) {
        long double P = 3.14;
        long double d, alpha, beta, a1, a2;
        long long int ans;
        // calculating the distance between the centers of the circles.
        d = sqrt((X2 - X1) * (X2 - X1) + (Y2 - Y1) * (Y2 - Y1));

        // checking if the circles are completely separate.
        if (d > R1 + R2)
            ans = 0;
        // checking if one circle is completely inside the other.
        else if (d <= abs(R2 - R1) && R1 >= R2)
            ans = (long long int)(P * R2 * R2);
        else if (d <= abs(R2 - R1) && R2 >= R1)
            ans = (long long int)(P * R1 * R1);
        // calculating the intersection area if the circles intersect.
        else {
            alpha = acos((R1 * R1 + d * d - R2 * R2) / (2 * R1 * d)) * 2;
            beta = acos((R2 * R2 + d * d - R1 * R1) / (2 * R2 * d)) * 2;
            a1 = (long double)0.5 * beta * R2 * R2 - 0.5 * R2 * R2 * sin(beta);
            a2 =
                (long double)0.5 * alpha * R1 * R1 - 0.5 * R1 * R1 * sin(alpha);
            ans = (long long int)(a1 + a2);
        }
        return ans;
    }
};

