// Back-end complete function Template for Java

class Solution {
    // Function to calculate the intersection area of two circles.
    long intersectionArea(double X1, double Y1, double R1, double X2, double Y2,
                          double R2) {
        // Constant value of PI.
        double P = 3.14;
        double d, alpha, beta, a1, a2;
        long ans;
        // Calculating the distance between the centers of the two circles.
        d = Math.sqrt((X2 - X1) * (X2 - X1) + (Y2 - Y1) * (Y2 - Y1));

        // Checking if the circles are completely separate (no intersection).
        if (d > R1 + R2)
            ans = 0;
        // Checking if one circle is completely inside the other (one is a subset of the other).
        else if (d <= Math.abs(R2 - R1) && R1 >= R2)
            ans = (long)(P * R2 * R2);
        else if (d <= Math.abs(R2 - R1) && R2 >= R1)
            ans = (long)(P * R1 * R1);

        // Calculating the intersection area if there is partial overlap between the circles.
        else {
            alpha = Math.acos((R1 * R1 + d * d - R2 * R2) / (2 * R1 * d)) * 2;
            beta = Math.acos((R2 * R2 + d * d - R1 * R1) / (2 * R2 * d)) * 2;
            a1 = (double)0.5 * beta * R2 * R2 - 0.5 * R2 * R2 * Math.sin(beta);
            a2 =
                (double)0.5 * alpha * R1 * R1 - 0.5 * R1 * R1 * Math.sin(alpha);
            ans = (long)(a1 + a2);
        }
        // Returning the final intersection area.
        return ans;
    }
};

