//User function Template for javascript
/**
 * @param {number} X1
 * @param {number} Y1
 * @param {number} R1
 * @param {number} X2
 * @param {number} Y2
 * @param {number} R2
 * @returns {number}
*/

class Solution {
    //Function to find the intersection area of two circles.
    intersectionArea(X1, Y1, R1, X2, Y2, R2) {
        const P = 3.14;
        let ans; // defining ans variable

        //Calculating the distance between the centers of the two circles.
        const d = Math.sqrt((X2-X1)*(X2-X1) + (Y2-Y1)*(Y2-Y1));

        //If the distance is greater than the sum of the radii, there is no intersection.
        if (d > R1+R2) {
            ans = 0;
        }

        //If the distance is less than the absolute difference of the radii and R1 is greater than or equal to R2,
        //then the smaller circle is completely inside the larger circle, so the intersection is equivalent to the area of the smaller circle.
        else if (d <= Math.abs(R2-R1) && R1 >= R2) {
            ans = P*R2*R2;
        }

        //If the distance is less than the absolute difference of the radii and R2 is greater than or equal to R1,
        //then the smaller circle is completely inside the larger circle, so the intersection is equivalent to the area of the smaller circle.
        else if (d <= Math.abs(R2-R1) && R2 >= R1) {
            ans = P*R1*R1;
        }

        //If none of the above conditions are satisfied, we calculate the intersection area using trigonometry.
        else {
            const alpha = Math.acos((R1*R1 + d*d - R2*R2) / (2*R1*d)) * 2;
            const beta = Math.acos((R2*R2 + d*d - R1*R1) / (2*R2*d)) * 2;
            const a1 = 0.5 * beta * R2*R2 - 0.5 * R2*R2 * Math.sin(beta);
            const a2 = 0.5 * alpha * R1*R1 - 0.5 * R1*R1 * Math.sin(alpha);
            ans = a1 + a2;
        }

        //Returning the integer value of the intersection area.
        return Math.floor(ans);
    }
}
