class Solution {
    static int inSequence(int a, int b, int c) {
        // calculate the difference between b and a
        int d = (b - a);
        // if the difference is 0, it means the sequence is in order
        if (d == 0) return 1;
        // if the difference is negative, check if c is also negative
        // if c is not negative and the difference is divisible by c, it means the
        // sequence is in order otherwise, the sequence is not in order
        if (d < 0) {
            if (c >= 0) return 0;
            if (d % c == 0) return 1;
            return 0;
        }
        // if the difference is positive, check if c is also positive
        // if c is not positive and the difference is divisible by c, it means the
        // sequence is in order otherwise, the sequence is not in order
        else {
            if (c <= 0) return 0;
            if (d % c == 0) return 1;
            return 0;
        }
    }
}

