 * @param {number} b
 * @param {number} c
 * @returns {number}
 */

class Solution {
    inSequence(a, b, c) {
        // code here
        let d = b - a;
        if (d === 0) return 1;
        if (d < 0) {
            if (c >= 0) return 0;
            if (d % c === 0) return 1;
            return 0;
        } else {
            if (c <= 0) return 0;
            if (d % c === 0) return 1;
            return 0;
        }
        return 0;
    }
}
