//Back-end complete function Template for C

void operations(int x, int y) {
    int p = x + y;
    int q = x - y;
    int r = x * y;
    int s = x / y;
    int t = x % y;

    printf("%d %d %d %d %d\n", p, q, r, s, t);
}

//Back-end complete function Template for C++

void operations(int x, int y) {
    int p = x + y;
    int q = x - y;
    int r = x * y;
    int s = x / y;
    int t = x % y;

    cout << p << " " << q << " " << r << " " << s << " " << t << endl;
}

//Back-end complete function Template for Java

public static void operations(int x, int y) {
    int p = x + y;
