public static ArrayList<Integer> fillArrayList(int arr[]) {
    int n = arr.length;
    // Declaring and Initialzing an ArrayList al of type Integer
    ArrayList<Integer> al = new ArrayList<>();

    // Adding the elements of the array to the ArrayList al
    for (int i = 0; i < n; i++) al.add(arr[i]);

    // returning the ArrayList
    return al;
}
