//Back-end complete function template in C++

class Solution{
    public:
    //Function to calculate the number of occurrences
    //of a character in a string after every kth position.
    int fun(string s, int k, int n, char c) {
        int ans=0;
        int sz=s.size();
        //iterating over the string
        for(int i=0;i<s.size();i++)
        {
            //if character at current position is equal to c
            if(s[i]==c)
                //calculating the number of occurrences
                //after every kth position
                ans+=(n-i+sz-1)/sz;
        }
        //returning the total number of occurrences
        return ans;
    }
};

