double posAverage(vector<int> arr) {
    int numberOfpos = 0;
    double sum = 0;

    for (int i = 0; i < arr.size(); i++) {
        if (arr[i] >= 0) {
            numberOfpos += 1;
            sum += arr[i];
        }
    }
    double avg = sum / numberOfpos;
    return avg;
}

