class Solution {
    static double posAverage(List<Integer> arr) {
        int cnt = 0;
        double sum = 0;
        for (int i = 0; i < arr.size(); i++) {
            if (arr.get(i) >= 0) {
                sum += arr.get(i);
                cnt += 1;
