public static double posAverage(int[] arr) {
    int numberOfpos = 0;
    int sum = 0;

    for (int x : arr) {
        if (x >= 0) {
            numberOfpos += 1;
            sum += x;
        }
    }
    double avg = (double)sum / numberOfpos;
    return avg;
}
