//Back-end complete function Template for C

double posAverage(int numbers[], int size){
    int numberOfpos = 0;
    int sum = 0;

    for (int i = 0; i < size; i++){
        if(numbers[i] >= 0){
            numberOfpos += 1;
            sum += numbers[i];
        }
    }
    double avg = (double) sum/numberOfpos;
    return avg;
}
