//Back-end complete function template for C++

class Solution {
  public:
    // Prints average of a stream of numbers
    vector<int> prefixAvg(vector<int> &arr) {
        int n = arr.size();
        vector<int> ans(n, 0); // initialize a vector to store the average values
        int avg = 0;           // initialize a variable to store the sum of numbers
        for (int i = 0; i < n; i++) { // iterate through the array
            avg += arr[i];            // add the current number to the sum
            ans[i] = avg / (i + 1); // calculate the average and store it in the vector
        }
        return ans; // return the vector of average values
    }
};

class Solution {
    // Prints average of a stream of numbers
    public ArrayList<Integer> prefixAvg(ArrayList<Integer> arr) {
        int n = arr.size();
        ArrayList<Integer> ans = new ArrayList<>(
            Collections.nCopies(n, 0)); // initialize a list to store the average values
        int avg = 0; // initialize a variable to store the sum of numbers
        for (int i = 0; i < n; i++) {  // iterate through the array
            avg += arr.get(i);         // add the current number to the sum
            ans.set(i, avg / (i + 1)); // calculate the average and store it in the list
        }
        return ans; // return the list of average values
    }
}
