// Back-end complete function Template for C++

class Solution{
public:
    // Function to check if the given number N satisfies the condition.
    int isPossible(int N){
        int x = 100 - N;

        // Check if the difference x is equal to any of the given numbers.
        if(x == 1 || x == 2 || x == 4 || x == 5 || x == 8 || x == 11)
            return 0; // Return 0 if N does not satisfy the condition.

        return 1; // Return 1 if N satisfies the condition.
    }
};

// Back-end complete function Template for Java

class Solution {
    // Function to check if a given number N is possible
    static int isPossible(int N) {
        // Calculate the difference between 100 and N
        int x = 100 - N;

        // Check if the difference is any of the specified values
        if (x == 1 || x == 2 || x == 4 || x == 5 || x == 8 || x == 11) {
            // If the difference is one of the specified values, return 0 (false)
            return 0;
        }

        // Otherwise, return 1 (true)
        return 1;
    }
}

