/*Function to print vector in reverse order
 * using iterator
 * it1 : v.rbegin()
 * it2 : v.rend()
 */
void reverseIterator(vector<int>::reverse_iterator it1,
                     vector<int>::reverse_iterator it2) {

    // iterating over the vector in reverse order
    for (auto it = it1; it != it2; it++) {
        // printing the value pointed by the iterator
        cout << *it << " ";
    }
    // printing a new line after printing the vector in reverse order
    cout << endl;
}

public class Solution
{
    public void reverseIterator(List<int> v)
    {
        v.Reverse(); // reverse the list
        foreach (var item in v)
        {
            Console.Write(item + " "); // print the reversed list
        }
        Console.WriteLine();
    }
}
