class Solution{
public:
    
    vector<string> convert(int a,int b,int c,string d)
    {
    
        // convert decimal number to binary equivalent
        string bin = "";
        if( a == 0)
        {
            bin = "0";
        }
        while(a)
        {
            int bx = a%2;
            bin += bx + '0';
            a/=2;
        }
        reverse(bin.begin(),bin.end());
        
        // convert binary number to decimal equivalent
        string decimal= "";
        int num = 0;
        int i = 0;
        if(b == 0)
            decimal = "0";
        while(b)
        {
            int bx = b%10;
            num = num + (bx*pow(2,i));
            i++;

            b=b/10;
        }
        
        // convert the number to string
        while(num)
        {
            int bx = num%10;
            decimal += (bx + '0');
            num /= 10;
        }
        reverse(decimal.begin(),decimal.end());
        
        // convert decimal number to hexa decimal equivalent
        string hex = "";
        if( c == 0)
        {
            hex = "0";
        }
        while(c)
        {
            int bx = c%16;
            if(bx<10)
                hex += bx + '0';
            else
                hex += ((bx - 10) + 'A');
            c/=16;
        }
        reverse(hex.begin(),hex.end());
        
        // convert the hexadecimal number to binary
        reverse(d.begin(),d.end());
        num = 0;
        for(int i = 0;i<d.length();i++)
        {
            if('0' <= d[i] && d[i] <= '9')
                num = num + ((d[i] - '0') * pow(16,i));
            else
                num = num + ((d[i] - 'A' + 10) * pow(16,i));
        }
        
        // convert the number to string
        string decimal2 = "";
        if(num == 0)
            decimal2 = "0";
        while(num)
        {
            int bx = num%10;
            decimal2 += bx + '0';
            num /= 10;
        }
        
        reverse(decimal2.begin(), decimal2.end());
        
        // build the answer vector
        vector<string> ans;
        ans.push_back(bin);
        ans.push_back(decimal);
        ans.push_back(hex);
        ans.push_back(decimal2);
        
        // return answer;
        return ans;
        
    }
};
